BiasLabelAssignment.Code:
function getBiasLabel(score) {
    if (score <= -2.5) return "Strong Bearish";
    if (score <= -1.5) return "Bearish";
    if (score <= -0.5) return "Mild Bearish";
    if (score >= 2.5) return "Strong Bullish";
    if (score >= 1.5) return "Bullish";
    if (score >= 0.5) return "Mild Bullish";
    return "Neutral";
}
